
// this is for express js 
const express = require('express');
const app = express();
const morgan = require('morgan');
const bodyParser = require('body-parser');

//this will logs the operation that are performed by the api user.
app.use(morgan('dev'));
app.use("/uploads" , express.static('uploads'));
app.use(bodyParser.urlencoded({extended:false}));
app.use(bodyParser.json());
// this will routes all to router.js file
const router  = require('./app/routes/router');

app.use((req,res,next)=>{
    res.header("Access-Control-Allow-Origin","*");
    res.header("Access-Control-Allow-Headers","*");
    if(req.method === 'OPTIONS'){
        req.header('Access-Control-Allow-Methods','GET,POST');
        return res.status(200).json({});
    }
    next();
});

app.use('/',router);


//hendal all the request and error.
app.use((req,res,next)=>{    
    const error = new Error('Not Found');
    error.status = 404;
    next(error);
});

// this will handel all the error from here.
app.use((error,req,res,next)=>{
    res.status(error.status || 500);
    res.json({
        message:'#Error! ' + error.message
    });
});


//export  to get that in entire application
module.exports = app;