let responder = require("../sender/response.sender");
let helperService = require("../service/helper.service");

let helperController = {
    getServerDate : getServerDate    
  };
  module.exports = helperController;

function getServerDate(req, res) {
    helperService.getServerDate(req, function(err, result) {
      responder.response(err, result, res);
    });
  }
  