const uploaderService = require('../service/uploader.service');
let access = require('../service/access.service');

let uploaderController = {
    uploadFile: fileUploader
}

module.exports = uploaderController;

function fileUploader(req, res) {
    access.getAccess(req.body.token, res, req.files, function (err, result) {
        if (err) {
            uploaderService.deleteUploadedFiles(req.files, res)
        }
        else {
            uploaderService.uploadFiles(req, res);
        }
    });
}


