const joi = require('joi');
module.exports = {
    body: {
        idEmployee: joi.number().optional().allow().empty(),
        nameEmployee: joi.string().required(),
        moNoEmployee: joi.string().required(),
        usernameEmployee: joi.string().required(),
        passwordEmployee: joi.string().optional().allow().empty(),
        isActiveEmployee: joi.boolean().required(),
        isDeleteEmployee: joi.boolean().required()
    }
};