const joi = require("joi");

module.exports = {
  body: {
    idEventSection: joi.number().allow("").allow(null).default(null),
    titleEventSection: joi.string().required(),
    descEventSection: joi.string().required(),
    sortDescEventSection: joi.string().required(),
    agendaEventSection: joi.string().required(),
    dateEventSection: joi.date().required(),
    lastDateOfEventSection: joi.date().required(),
    imageUrlEventSection: joi.string().required(),
    prevYearVideoUrlEventSection: joi.string().required(),
    isActiveEventSection: joi.boolean().required(),
    token: joi.string().required(),
    employeeId: joi.number().required()
  }
};
