const joi = require('joi');

module.exports = {
    body: {
        idSliderSection : joi.number().allow('').allow(null).default(null),        
        captionSliderSection: joi.string().allow('').allow(null).default(''),
        titleSliderSection: joi.string().allow('').allow(null).default(''),
        isActiveSliderSection: joi.boolean().required(),
        token: joi.string().required(),
        employeeId: joi.number().required(),
        orderSliderSection: joi.number().allow('').allow(null).default(null)
    }
};