const express = require('express');

let responseSender = {
    response : sendResponse,
    valiodationFail : invalidDataInput,
    invalidLogin : invalidLogin
}

module.exports = responseSender;

function sendResponse(err,result,res) {    
    if(err){                   
        res.send({
            status: 404,
            message: "#Error! not found"
        });        
    }        
    else{
        res.send({
            status: 200,
            message: "OK",
            data:result
        });
    }
}

function invalidDataInput(res) {                         
        res.send({
            status: 422,
            message: "#Validation Fail! Unprocessable Entity."
        });           
}

function invalidLogin(res){
    res.send({
        status: 422,
        message: "#Invalid Login! Unprocessable Entity."
    });
}